import _isEmpty from "lodash/isEmpty";
import React from 'react';
import View from "../../components/view";
import Text from "../../components/text";

const HeaderContent = props => {
  const {
    text = {}
  } = props;
  const {
    title,
    titleStyle,
    titleProps,
    subtitle,
    subtitleStyle,
    subtitleProps
  } = text;

  if (title || subtitle) {
    return <View marginH-s5 marginV-s1>
        {!_isEmpty(title) && <Text $textDefault {...titleProps} marginB-s3 style={titleStyle}>
            {title}
          </Text>}
        {!_isEmpty(subtitle) && <Text $textDefault {...subtitleProps} marginB-s3 style={subtitleStyle}>
            {subtitle}
          </Text>}
      </View>;
  }

  return null;
};

export default React.memo(HeaderContent);